import React from "react";
import { MDBCol,MDBIcon, MDBCard, MDBCardImage,MDBCardBody,MDBCardTitle,MDBCardText,MDBBtn, MDBRow, MDBFooter, MDBContainer } from "mdbreact";

import icon_white_reliable from './../images/icon_white_reliable.png';
import icon_white_secure from './../images/icon_white_secure.png';
import icon_white_affordable from './../images/icon_white_affordable.png';
import daburch from './../images/daburch.jpeg';
import './../css/removecolor.css';
var bstyle={
    display: "flex",
    flexwrap: "wrap",
    marginRight: 0,
    marginLeft: 0};
const borderradius={
    borderRadius: '0.25rem'
}

var cdstyle={border:"dotted 1px green"};
const DescribeProduct = () => {
  return (

<MDBRow className="ml-0 mr-0">
<MDBCol lg="4" md="12" sm="12" className="mb-lg-0 mb-4">
  <MDBCard className="h-100  mt-4  ml-1 " style={{boxShadow:"none",border:"none"}} >

    <MDBRow className="h-85 border gray mb-1 " style={{...bstyle,...borderradius}}><img className="mx-auto" src={daburch} alt="dabur" style={{width:'100%'}} /></MDBRow>
    <MDBRow className="h-15"  style={bstyle}>
        <MDBCol className=" border gray mr-1" style={borderradius}><img className="mx-auto " src={daburch}  style={{width: "100%", height: "100%"}}alt="dabur" /></MDBCol>
        <MDBCol className=" border gray mr-1" style={borderradius}><img className="mx-auto" src={daburch}  style={{width: "100%", height: "100%"}}alt="dabur" /></MDBCol>
        <MDBCol className=" border gray mr-1" style={borderradius}><img className="mx-auto" src={daburch}  style={{width: "100%", height: "100%"}}alt="dabur" /></MDBCol>
        <MDBCol className=" border gray mr-1" style={borderradius}><img className="mx-auto" src={daburch}  style={{width: "100%", height: "100%"}}alt="dabur" /></MDBCol>
    </MDBRow>
  </MDBCard>
</MDBCol>
<MDBCol lg="5" md="7" sm="12" className="mb-lg-0 mb-4">
  <MDBCard className="h-100 border gray mt-4 ml-1">
   </MDBCard>
</MDBCol>
<MDBCol lg="3" md="5" sm="12" className="mb-lg-0 mb-4">
  <MDBCard className="h-100 mt-4 ml-1" style={cdstyle}>
   <MDBRow className="h-20 mr-0 ml-0 justify-content-center" style={{backgroundColor:"#23db23"}}><p style={{color:"white",textTransform:'uppercase',fontSize:'14px'}} className=" pb-0 mt-1 mb-1"><b>Additional Offers</b></p></MDBRow>
   <MDBRow className="h-80 mr-0 ml-0"><p style={{color:"gray",fontSize:12}} className="pl-2 pb-0 text-left"><b>Amazon Pay:</b>Amazon Pay uses the consumer base of Amazon.com and focuses on giving users the option to pay with their Amazon accounts on external merchant websites. </p></MDBRow>
   <MDBRow className="h-80 mr-0 ml-0"><p style={{color:"gray",fontSize:12}} className="pl-2 pb-0 text-left"><b>PayPal:</b>Amazon Pay uses the consumer base of Amazon.com and focuses on giving users the option to pay with their Amazon accounts on external merchant websites. </p></MDBRow>
   <MDBRow className="h-80 mr-0 ml-0"><p style={{color:"gray",fontSize:12}} className="pl-2 pb-0 text-left"><b>Special Offer:</b>Amazon Pay uses the consumer base of Amazon.com and focuses on giving users the option to pay with their Amazon accounts on external merchant websites. </p></MDBRow>
   <MDBRow className="h-80 mr-0 ml-0"><p style={{color:"gray",fontSize:12}} className="pl-2 pb-0 text-left"><b>Ola Money Postpaid:</b>Amazon Pay uses the consumer base of Amazon.com and focuses on giving users the option to pay with their Amazon accounts on external merchant websites. </p></MDBRow>
  </MDBCard>
</MDBCol>
</MDBRow>

  );
}

export default DescribeProduct;
